SELECT teacher.name,course_type,course.name FROM `teaching`,`teacher`,`class`,`course` WHERE (teacher.name = '贝佳（副教授）' OR teacher.name = '黄蕾（助教）' OR teacher.name = '王浩然（副教授）' OR teacher.name = '张瑾玉（讲师）' OR teacher.name = '丁二玉（讲师）' OR teacher.name LIKE '%荣国平%' OR teacher.name = '刘嘉（讲师）') AND teaching.teacher_id = teacher.id AND class.id = teaching.class_id AND course.id = class.course_id
